import React, { useEffect, useState } from 'react';
import "./processtree_style.css"
import Tree from './Tree';
interface Params{
    back_button:()=>void
}


function ProcessTree({back_button}:Params){
    let [nodes,set_nodes] = useState([])
    let [edges,set_edges] = useState([])
    let [elm_list,set_elm_list] = useState([<></>])
    useEffect(() => {
        let algo = fetch("http://localhost:1200/all_pids")
        algo.then((res)=>{
            res.json().then((obj)=>{
                let options = [] 
                for (let i=0;i<obj.length;i++) {
                    options.push(<option value={obj[i].Pid}>{obj[i].Pid}</option>)
                }
                set_elm_list(options)
            })
        })
      }, []);
    // let elm_list = [<option value="value1">Value 1</option>,
    // <option value="value2" selected>Value 2</option>,
    // <option value="value3">Value 3</option>];

    return <div className='processtree-page'>
        <button onClick={back_button}>Back</button>
            <div className='process-pid-select'>
                <p>Proceess pid</p>
                <select name="select-pid">{elm_list}</select>
            </div>
            <Tree edges={edges} nodes={nodes}></Tree>
    </div>
}

export default ProcessTree;
