import React, { useEffect, useRef, useState } from 'react';
import "./processsim_style.css"
import Tree from './Tree';
import { Network } from "vis-network";

interface Params{
    back_button:()=>void
}

function ProcessSim({back_button}:Params){
    let node1 = { id: 1, label: "New" }
    let node2 = { id: 2, label: "Ready" }
    let node3 = { id: 3, label: "Runing", color:""}
    let edge1 = { from: 1, to: 2 ,arrows:"to"}
    let edge2 = { from: 2, to: 3 ,arrows:"to", color:""}
    let edge3 = { from: 3, to: 2 ,arrows:"to"}
    let nodes = [] as any[]
    let edges = [] as any[]
    let [trgt_pid,set_trgt_pid] = useState(0)
    let last_node = 0
    const visJsRef = useRef<HTMLDivElement>(null);
    const options = {};
                let network = visJsRef.current 
                && new Network(visJsRef.current, { nodes, edges }, options);
    let new_action = ()=>{
        let algo = fetch("http://localhost:1200/new_process")
        algo.then((res)=>{
            res.json().then((obj)=>{
                set_trgt_pid(obj.Pid)
                fetch("http://localhost:1200/save_op_process",{ method:'POST', body:`{"pid":${obj.Pid},"operation":1}`})
                fetch("http://localhost:1200/save_op_process",{ method:'POST', body:`{"pid":${obj.Pid},"operation":2}`})
                fetch("http://localhost:1200/save_op_process",{ method:'POST', body:`{"pid":${obj.Pid},"operation":3}`})
                nodes.push(node1)
                nodes.push(node2)
                node3.color = "red"
                nodes.push(node3)
                edges.push(edge1)
                edge2.color = 'red'
                edges.push(edge2)
                last_node = 3
                network = visJsRef.current 
                && new Network(visJsRef.current, { nodes, edges }, options);
                network?.redraw90
            })
        })
    }
    let stop_action = ()=>{
        fetch("http://localhost:1200/save_op_process",{ method:'POST', body:`{"pid":${trgt_pid},"operation":2}`})
        // 3->2
        last_node = 2

    }
    let resume_action = ()=>{
        fetch("http://localhost:1200/save_op_process",{ method:'POST', body:`{"pid":${trgt_pid},"operation":3}`})
        // 2->3
        last_node = 3
    }
    let kill_action = ()=>{
        fetch("http://localhost:1200/save_op_process",{ method:'POST', body:`{"pid":${trgt_pid},"operation":4}`})
        let last_node = { id: 4, label: "Terminated", color:'red'}
        nodes.push(last_node)
        edges.push({ from: last_node, to: 4 ,color:'red', arrows:"to"})
    }


    return <div className='processsim-page'>
        <button onClick={back_button} id='back'>Back</button>
        <div id='process-pid-on'>
            <p>Proceess pid: </p>
            <p id='trgt-pid'>{trgt_pid}</p>
        </div>
        <div id='buttons-box'>
            <button className='sim-buttons' onClick={new_action}>New</button>
            <button className='sim-buttons' onClick={stop_action}>Stop</button>
            <button className='sim-buttons' onClick={resume_action}>Resume</button>
            <button className='sim-buttons' onClick={kill_action}>Kill</button>
        </div>
        {/* <Tree edges={edges} nodes={nodes}></Tree> */}
        <div ref={visJsRef} style={{
        height: '80%',
        width: '100%',
        backgroundColor:'black',
        display:'flex',
        flexGrow:1,
    }} />
    </div>
}

export default ProcessSim;
