import React, { useState } from 'react';
import "./historicaltimemonitor_style.css"
import LineChart from './LineChart';




interface Params{
    back_button:()=>void
}

function HistoricMonitor({back_button}:Params){

    let [data,set_data] = useState([30,70,24,64])

    useEffect(() => {
        // Use setTimeout to update the message after 2000 milliseconds (2 seconds)
        const timeoutId = setTimeout(() => {
            let algo = fetch("http://localhost:1200/realtime")
            algo.then((res)=>{
                res.json().then((obj)=>{

                    set_ram_data([obj.Ram,100-obj.Ram])
                    set_cpu_data([obj.Cpu,100-obj.Cpu])  
                })
            })
        }, 2000);

        // Cleanup function to clear the timeout if the component unmounts
        return () => clearTimeout(timeoutId);
      }, [ram_data,cpu_data]);

    return <div className='realtimemonitor-page'>
        <button onClick={back_button}>Back</button>
        <div className='container-realtimemonitor'>
            <LineChart data_set={data} title={"CPU Usage"}></LineChart>
            <LineChart data_set={data} title={"RAM Usage"}></LineChart>
        </div>
    </div>
}

export default HistoricMonitor;
